mapper.select:
select B.* 
from BOOK B, MEMBERLIKEBOOKS MLB, MEMBER M 
where (M.id=MLB.id and MLB.num=B.num) and M.id=#{id}

select B.* 
from BOOK B, BUYERBUYBOOKS BBB, MEMBER M 
where (M.id=BBB.id and BBB.num_buy=B.num) and M.id=#{id}

select B.* 
from BOOK B, BUYERBUYBOOKS BBB, MEMBER M 
where (M.id=BBB.id and BBB.num_buy=B.num) and M.id=#{id}
